package Basicsofjava;

public class NestedLoopsTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int weeks=3;
int days=7;
//the outer loop is for weeks
for(int i=1;i<=weeks;i++) {
	System.out.println("The weeks " +i);
	for(int j=1;j<=days;j++) {
		System.out.println("The days " +j);}
	
		for(int a=1;a<=5;a++) {
			System.out.println("print time "+a);
		}
	}
}
	}


